sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {


    function showOrHideFunction(value) {
        var array = ["action_needed", "date_of_last_update", "date_of_last_update",
            "eoir_filing_deadline", "hearing_location", "hearing_type", "ice_deadline_ck_in", "other_deadline",
            "priority_date", "responsible_office", "state","hearing_date", "uscis_filing_deadline"
        ];

        for (var i = 0; i < array.length; i++) {
			if(!value){
				g_form.clearValue(array[i]);
			}
            g_form.setMandatory(array[i], value);
        }
        g_form.setSectionDisplay('immigration', value);

    }


    //Type appropriate comment here, and begin script below

function functionAjax(){
    var matterValues = g_form.getValue("matter_type");
    var probonoGlide = new GlideAjax("ProBonov2");
    probonoGlide.addParam("sysparm_name", "tempFunction");
    probonoGlide.addParam("matterArrays", matterValues);
    probonoGlide.getXML(callback);

    function callback(response) {
        var finalAnswer = response.responseXML.documentElement.getAttribute("answer");
		var showOrHideImmigration ;
        if (finalAnswer == "true") {
            showOrHideImmigration = true;
            showOrHideFunction(showOrHideImmigration);
        } else {
            showOrHideImmigration = false;
            showOrHideFunction(showOrHideImmigration);

        }
    }
}

functionAjax();

  if (isLoading || newValue === '') {
		functionAjax();
        return;
    }




}
