sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var clientMatterValue = g_form.getValue("existing_client_matter");

    var fields = ["keni_request_number", "matter_type", "referal_source",
       "matter_name", "client_name", "representation_type", "matter_description", "date_of_keni_request_submission", "matter_office", "language_requirement", "client_type"
    ];

    if (clientMatterValue != "") {
        var populateAjax = new GlideAjax("PopulateClientDetails");
        populateAjax.addParam("sysparm_name", "populateDetails");
        populateAjax.addParam("recordNumber", clientMatterValue);
        populateAjax.getXML(callback);

    }

    function callback(response) {
        var ans = response.responseXML.documentElement.getAttribute("answer");
		alert(ans);
        if (ans != false) {

			var newObject=JSON.parse(ans);
			for(var eachField=0;eachField<fields.length;eachField++){
			g_form.setValue(fields[eachField],newObject[fields[eachField]]);
			}            
        }

    }


    //Type appropriate comment here, and begin script below

}
