var ProBonov2 = Class.create();
ProBonov2.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	tempFunction:function(){
		var getDetail=this.getParameter("matterArrays");
		var detail=getDetail.split(",");


	for(var i=0;i<detail.length;i++){
	var foundData=new GlideRecord("x_1268707_pro_bono_foundation_data");
	foundData.get(detail[i].toString());
	var value=foundData.getValue("value");
	var detailValue=value.toLowerCase();
	if(detailValue.includes("immigration")){
		return "true";
	}
	}
	 return "false";


	},

    type: 'ProBonov2'
});
