sys_script_include.script:
var PopulateClientDetails = Class.create();
PopulateClientDetails.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	populateDetails:function(){
		var record_number=this.getParameter("recordNumber");
		var fields = ["keni_request_number", "matter_type", "referal_source",
       "matter_name", "client_name", "representation_type", "matter_description", "date_of_keni_request_submission", "matter_office", "language_requirement", "client_type"
    ];

	    var getOpportunity=new GlideRecord("x_1268707_pro_bono_pro_bono_opportunity");
		getOpportunity.addQuery("sys_id",record_number);
		getOpportunity.query();
		if(getOpportunity.next()){
			var tempObject={};
			for(var i=0;i<fields.length;i++){
				tempObject[fields[i]]=getOpportunity.getDisplayValue([fields[i]]);
			//  getOpportunity.getDisplayValue("matter_name");
			}
			return JSON.stringify(tempObject);
		}

		return false;
	},

    type: 'PopulateClientDetails'
});
