sys_script_include.script:
var GetDetails = Class.create();
GetDetails.prototype = {
    initialize: function() {
    },
	recordFunction:function(){




		var temp_array=[];
		var timeKee_array=[];
		var table=new GlideRecord("x_1268707_pro_bono_timekeeper_register");

		table.query();
		while(table.next()){
		var sys_id=table.getValue("user");
		if(sys_id!=null){
		timeKee_array.push(sys_id);
		}
		}



		var userTable=new GlideRecord("sys_user");
		userTable.addQuery("active",true);
		userTable.query();
		while(userTable.next()){
		var id=userTable.getValue("sys_id");
		if(timeKee_array.includes(id)==false){
		temp_array.push(id);
		}
    //    temp_array=["005d500b536073005e0addeeff7b12f4","02826bf03710200044e0bfc8bcbe5d3f"]
		}
		return "sys_idIN"+temp_array;


	},

    type: 'GetDetails'
};
