sys_script_include.script:
var staffRequirementSubmit = Class.create();
staffRequirementSubmit.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    submitFunction: function() {

        // var getDetails=this.getParameter("")

        var number = this.getParameter("number").toString();
        var timekeeper_office = this.getParameter("timekeeper_office");
        var timekeeper_type = this.getParameter("timekeeper_type");
        var practice_group = this.getParameter("practice_group");
        var subpractice_group = this.getParameter("subpractice_group");
        var notes = this.getParameter("notes");
		var date=this.getParameter("date");
		var id=this.getParameter("oppor_reference");

        var gr = new GlideRecord("x_1268707_pro_bono_staffing_requirements");
        gr.initialize();
        gr.setValue("number", number);
        gr.setValue("timekeeper_office", timekeeper_office);
        gr.setValue("timekeeper_type", timekeeper_type);
        gr.setValue("practice_group", practice_group);
        gr.setValue("subpractice_group", subpractice_group);
        gr.setValue("notes", notes);
		gr.setValue("law_school_year",date);
		gr.setValue("opportunity",id);
        gr.insert();

        return "Record Inserted";
    },
    type: 'staffRequirementSubmit'
});
