sys_script_include.script:
var addDetailsToOutreach = Class.create();
addDetailsToOutreach.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    addRecordFunction: function() {

        var sys_ids = this.getParameter("sys_ids");
        var recordArray = sys_ids.split(",");
		var oppo_id = gs.getProperty("x_1268707_pro_bono.Store_opportunity_sys_id");

        for (var i = 0; i < recordArray.length; i++) {
            var timekeeperTable = new GlideRecord("x_1268707_pro_bono_timekeeper_register");
            timekeeperTable.get(recordArray[i]);

                var table = new GlideRecord("sn_customerservice_related_party");
				table.addQuery("x_1268707_pro_bono_timekeeper",recordArray[i]);
				table.addQuery("x_1268707_pro_bono_opportunity",oppo_id);
				table.query();
				if(!table.next()){
                table.initialize();
                table.x_1268707_pro_bono_timekeeper_name = timekeeperTable.timekeeper_name;
                table.x_1268707_pro_bono_timekeeper_email = timekeeperTable.timekeeper_email;
				table.x_1268707_pro_bono_timekeeper=recordArray[i];
				table.x_1268707_pro_bono_sub_practice=timekeeperTable.timekeeper_sub_practice_group;
				table.x_1268707_pro_bono_opportunity=oppo_id;
                table.insert();
				}


        }

        return oppo_id;

    },

    type: 'addDetailsToOutreach'
});
